//   left: 10px;
// `;

const NoData = styled.p`
  font-size: 1.2rem;
  color: var(--gray-900);
  margin-top: 20px;
`;

const ProductList = ({ isLoading, title, datas }) => {
  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (datas.length === 0) {
    return <NoData>상품이 없습니다.</NoData>;
  }

  return (
    <>
      <h2>{title}</h2>
      <List>
        {datas.map((data) => (
          <ProductItem key={data.id}></ProductItem>
        ))}
      </List>
    </>
  );
};

export default ProductList;
