import styled from "styled-components";

const Image = styled.img`
  width: 100%;
  height: 70%;
  object-fit: cover;
`;

const Title = styled.h3`
  font-size: 1.2rem;
  font-weight: bold;
  margin-top: 10px;
  margin-bottom: 5px;
  color: var(--gray-900);
`;

const Price = styled.p`
  font-size: 1rem;
  margin-top: 5px;
  color: var(--gray-700);
`;

const ProductItem = ({ title, imageUrl, price }) => {
  return (
    <>
      <Image src={imageUrl} alt={title} />
      <Title>{title}</Title>
      <Price>{`${price}원`}</Price>
    </>
  );
};

export default ProductItem;
