import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import ProductItem from "./ProductItem";

const List = styled.ul`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
`;

const Item = styled.li`
  width: 250px;
  height: 350px;
  margin: 20px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
  overflow: hidden;
  position: relative;
`;

const Image = styled.img`
  width: 100%;
  height: 60%;
  object-fit: cover;
`;

const Title = styled.h3`
  font-size: 1.2rem;
  font-weight: bold;
  margin-top: 10px;
  margin-bottom: 5px;
  color: var(--gray-900);
  position: absolute;
  bottom: 50px;
  left: 0;
  width: 100%;
  padding: 0 10px;
  background-color: rgba(255, 255, 255, 0.9);
  z-index: 1;
`;

const Price = styled.p`
  font-size: 1rem;
  margin-top: 5px;
  color: var(--gray-700);
  position: absolute;
  bottom: 30px;
  left: 10px;
`;

const NoData = styled.p`
  font-size: 1.2rem;
  color: var(--gray-900);
  margin-top: 20px;
`;

const ProductList = ({ isLoading, title, datas }) => {
  if (isLoading) {
    return <p>Loading...</p>;
  }

  if (datas.length === 0) {
    return <NoData>상품이 없습니다.</NoData>;
  }

  return (
    <>
      <h2>{title}</h2>
      <List>
        {datas.map((item) => (
          <ProductItem>item={item}</ProductItem>
        ))}
      </List>
    </>
  );
};

export default ProductList;
