import React from "react";
import styled from "styled-components";

const ItemContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;

  border-radius: 10px;
`;

const ImageBox = styled.div`
  width: 100%;
  height: 250px;
  margin-bottom: 5px;
  overflow: hidden;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  border-radius: 5px;
`;

const DetailContainer = styled.div`
  text-align: center;
`;

const Title = styled.h1`
  font-size: 1.2rem;
  margin-bottom: 5px;
  color: #333;
`;

const Brand = styled.span`
  font-weight: bold;
  color: #777;
`;

const Discount = styled.span`
  font-weight: bold;
  color: red;
`;

const Numbers = styled.span`
  font-size: 0.9rem;
  color: #777;
`;

const ProductItem = ({ item }) => {
  return (
    <ItemContainer>
      <ImageBox>
        <Image src={item.image_url ? item.image_url : item.brand_image_url} />
      </ImageBox>
      <DetailContainer>
        {item.title ? (
          <Title>{item.title}</Title>
        ) : (
          <Title>{item.brand_name}</Title>
        )}
        {(() => {
          switch (item.type) {
            case "Brand":
              return <Brand>관심고객수</Brand>;
            case "Product":
              return <Discount>{item.discountPercentage}%</Discount>;
            default:
              return null;
          }
        })()}
      </DetailContainer>
      <DetailContainer>
        {item.sub_title && <span>{item.sub_title}</span>}
        <Numbers>
          {(() => {
            switch (item.type) {
              case "Product":
                return `${item.price
                  .toString()
                  .replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ",")}원`;
              case "Brand":
                return item.follower
                  .toString()
                  .replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ",");
              default:
                return null;
            }
          })()}
        </Numbers>
      </DetailContainer>
    </ItemContainer>
  );
};

export default ProductItem;
