import { useState } from "react";
import styled from "styled-components";
import { useDispatch, useSelector } from "react-redux";

const Container = styled.div`
  height: 264px;
  width: 264px;
`;

const ImageWrapper = styled.div`
  width: 264px;
  height: 210px;
  border-radius: 12px;
  overflow: hidden;
  position: relative;
  cursor: pointer;

  & img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const TitleWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 6px;
  font-weight: 800;
  font-size: 16px;
  cursor: pointer;

  & :nth-child(2) {
    color: ${(props) =>
      props.type === "Product" ? props.theme.colors.purple : "black"};
  }
`;

const SubtitleWrapper = styled.div`
  margin-top: 3px;
  font-size: 16px;
  font-weight: 400;
  text-align: ${(props) => (props.type === "Exhibition" ? "start" : "end")};
  cursor: pointer;
`;

const Item = ({
  id,
  type,
  title,
  sub_title,
  brand_name,
  price,
  discountPercentage,
  image_url,
  brand_image_url,
  follower,
}) => {
  let titleLeft = title || brand_name;
  let titleRight = "";
  let subtitle = "";
  let imageUrl = image_url;

  const dispatch = useDispatch();

  if (type === "Product") {
    titleRight = `${discountPercentage}%`;
    subtitle =
      Number(price).toLocaleString("ko-KR", { currency: "KRW" }) + "원";
  } else if (type === "Exhibition") {
    subtitle = sub_title;
  } else if (type === "Brand") {
    titleRight = "관심고객수";
    subtitle = Number(follower).toLocaleString("ko-KR", { currency: "KRW" });
    imageUrl = brand_image_url;
  }

  return (
    <>
      <Container>
        <ImageWrapper>
          <img src={imageUrl} alt="img" />
        </ImageWrapper>

        <TitleWrapper type={type}>
          <p>
            {type === "Category" && "# "}
            {titleLeft}
          </p>
          <p>{titleRight}</p>
        </TitleWrapper>
        <SubtitleWrapper type={type}>{subtitle}</SubtitleWrapper>
      </Container>
    </>
  );
};

export default ProductItem;
